example.code:
		
private void button1_Click(object sender, EventArgs e) 
{ 
Form1 objForm1 = new Form1(); 
objForm1.Closed += objForm1_Closed; 
objForm1.ShowDialog(this); 
} 
private void objForm1_Closed(object sender, EventArgs e) 
{ 
if (((Form1)sender).DialogResult == DialogResult.OK) { 
MessageBox.Show("Dialog result OK"); 
} 
}

		
